import React, { useState } from 'react';

const baseURL = 'https://strangers-things.herokuapp.com/api/2209-FTB-MT-WEB-PT'


const Register = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const handleSubmit = async () => {
        try {
            const response = await fetch(`${baseURL}/users/register`, {
              method: "POST",
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify({
                user: {
                  username: {username},
                  password: {password}
                }
              })
            })
            const result = await response.json();
            return result;
          } catch(error) {
            console.log('error registering user')
          }
          if (result.success) {
            console.log(result.data.token)
          }
        }

    return (
        <div id="home">

            <h1>Registration:</h1>

            <div className='login-div'>
                <form onSubmit={(event) => {
                event.preventDefault();
                handleSubmit();
                }}>
                <label className='bold'>Please enter your desired username:</label><br></br>
                <input type='text'onChange={(event) => setUsername(event.target.value)}/>
                <br></br>
                <br></br>
                <label className='bold'>Please enter your desired password:</label>
                <br></br>
                <input
                type='password'
                onChange={(event) => setPassword(event.target.value)}/>
                <br></br>
                <br></br>
                <button type='submit'>Submit</button>
                </form>
            </div>

         </div>
)
}

export default Register;
