import React, { useEffect, useState } from 'react';

const Posts = () => {
    const [isLoaded, setIsLoaded] = useState(false);
    const [posts, setPosts] = useState([]);

    useEffect(() => {
            fetch("https://strangers-things.herokuapp.com/api/2209-FTB-MT-WEB-PT/posts")
                .then(response => response.json())
                .then(
                    (result) => {
                        setIsLoaded(true)

                        let res = [];

                        for(let i in result) {
                            res.push(result[i])
                        }
                        console.log(res);
                        console.log(res[2]);
                        console.log(res[2].posts)
                        console.log(res[2].posts[0].title)
                        setPosts(res[2].posts)
                        console.log(res)
                    }
                )
    }, [])

    return (
        <div id="posts">
            <h1>Posts</h1>
            <ul>
                <li>"Post title goes here"</li>
                <li>"Post description goes here"</li>
                <li>Price:</li>
                <li>Seller:</li>
                <li>Location:</li>
            </ul>     
        </div>
    )
}

export default Posts;

