import React, { useEffect, useState } from 'react';

const Posts = () => {
    const [isLoaded, setIsLoaded] = useState(false);
    const [posts, setPosts] = useState([]);

    useEffect(() => {
            fetch("https://strangers-things.herokuapp.com/api/2209-FTB-MT-WEB-PT/posts")
                .then(response => response.json())
                .then(
                    (result) => {
                        setIsLoaded(true)

                        // let res = [];

                        // for(let i in result) {
                        //     res.push(result[i])
                        // }
                        console.log(result);
                        // console.log(res.data);
                        // console.log(res[2].posts)
                        // console.log(res[2].posts[0].title)
                        setPosts(result.data.posts)
                        console.log(result.data.posts)
                    }
                )
    }, [])

    return (
        <div id="posts">
            <h1>Place Holder Heading</h1>

            {
                posts && posts.map((post) => {
                    return (
                        <div>
                            <h2>{post.title}</h2> <br></br>
                        <h2>{post.description}</h2> <br></br>
                        <h3>{post.price}</h3> <br></br>
                        </div>
                    )
                })
            }  
        </div>
    )
}

export default Posts;

